mapper.select:
select
cp_id
from tb_company
where cp_name = #{cp_Name} and cp_ceo = #{cp_Ceo}
and cp_bnum = #{cp_Bnum}

select cp_id
from tb_company
where cp_id=#{cp_Id} and cp_name=#{cp_Name}
and cp_ceo=#{cp_Ceo} and cp_bnum=#{cp_Bnum}

mapper.update:
update tb_company
set
cp_pw = #{cp_Pw}
where cp_Id = #{cp_Id}

update
tb_compsecurity
set cp_id = #{cp_Id},
salt = #{salt}
where cp_id =
#{cp_Id}

update tb_reserv
set res_State = 2
where mem_Id = #{mem_Id} and res_Num = #{res_Num}
