mapper.select:
select mem_num, mem_id, mem_pw, mem_name, mem_birth, mem_phone, mem_email, mem_job, mem_state 
from tb_mem
where mem_id = #{mem_Id}

mapper.update:
update tb_security
set mem_Id = #{mem_Id},
salt = #{salt}
where mem_ID = #{mem_Id}

mapper.insert:
/* mem - memInsert */
select tb_mem_seq.nextval from dual
insert into tb_mem(mem_num, mem_id, mem_pw, mem_name, mem_birth, mem_phone, mem_email, mem_job) values(#{idx}, #{mem_Id}, #{mem_Pw}, #{mem_Name}, #{mem_Birth}, #{mem_Phone}, #{mem_Email}, #{mem_Job})

mapper.update:
update tb_mem set
mem_pw=#{mem_Pw}, 
mem_phone=#{mem_Phone}, mem_email=#{mem_Email}, mem_job=#{mem_Job}
where mem_Id=#{mem_Id}

mapper.delete:
update tb_mem set 
mem_state = 1 
where mem_id = #{mem_Id}

mapper.select:
select mem_id
from tb_mem
where mem_name = #{mem_Name} and mem_Phone = #{mem_Phone}

mapper.update:
update tb_mem
set mem_Pw = #{mem_Pw}
where mem_Id = #{mem_Id}
