mapper.insert:

	select tb_notice_seq.nextval from dual

insert into tb_notice(
	nt_num, nt_title, nt_content, ad_num , nt_file
)
values(
	#{nt_num}, #{nt_title}, #{nt_content}, #{ad_num}, #{nt_file}
)

mapper.update:
update tb_notice
	set nt_title = #{nt_title},
		nt_content = #{nt_content}
		, nt_file = #{nt_file}
	where nt_num = #{nt_num}
