mapper.select:

/* qna - qnaList */ 
SELECT qna_Num, qna_Name, qna_Title,
	TO_CHAR(qna_Date,'YYYY-MM-DD HH24:MI:SS') AS qna_Date   
FROM  
(  SELECT list.*, ROWNUM AS RNUM  
	FROM ( 



				ORDER BY qna_Num  


				ORDER BY qna_Date    




				ASC 


				DESC 


	) list 
) 




			RNUM BETWEEN #{start_row} AND #{end_row}


/* qna - qnaList */ 
SELECT qna_Num, qna_Name, qna_Title,  
	TO_CHAR(qna_Date,'YYYY-MM-DD') AS qna_Date   
FROM  
(  SELECT list.*, ROWNUM AS RNUM  
	FROM ( 



				ORDER BY qna_Num  


				ORDER BY qna_Date    




				ASC 


				DESC 


	) list 
) 




			RNUM BETWEEN #{start_row} AND #{end_row}

/* Qna - qnaListCnt */
SELECT NVL(count(1), 0) FROM 
( 	SELECT list.*, ROWNUM AS RNUM 
	FROM (

			ORDER BY qna_Num desc
	) list
)

/* Qna - qnaListCnt */
SELECT NVL(count(1), 0) FROM 
( 	SELECT list.*, ROWNUM AS RNUM 
	FROM (

			ORDER BY qna_Num desc
	) list
)

/* Qna - qnaDetail */
SELECT
	      qna_Num, qna_Name, qna_Title, qna_Content, 
	      TO_CHAR(qna_Date,'YYYY-MM-DD HH24:MI:SS') AS qna_Date
FROM tb_qna
WHERE qna_Num = #{qna_Num}

/* Qna - qnaDetail2 */
SELECT
	      qna_Num, qna_Name, qna_Title, qna_Content, 
	      TO_CHAR(qna_Date,'YYYY-MM-DD HH24:MI:SS') AS qna_Date
FROM tb_qna2
WHERE qna_Num = #{qna_Num}

mapper.insert:
/* Qna - qnaInsert */

	select tb_qna_seq.nextval from dual

	INSERT INTO tb_qna(
			qna_Num, 
			qna_Name, 
			qna_Title, 
			qna_Content, 
			qna_Pw
	)
	VALUES(
			#{qna_Num}
		   ,#{qna_Name}
		   ,#{qna_Title}
		   ,#{qna_Content}
		   ,#{qna_Pw}
	)

mapper.select:
/* Qna - pwdConfirm */
SELECT NVL((
	          SELECT 1 FROM tb_qna
	           WHERE qna_Num = #{qna_Num}
	               AND qna_Pw = #{qna_Pw}
	       ), 0) as state
FROM dual

mapper.update:
/* Qna - qnaUpdate */
UPDATE tb_qna
	   SET qna_Title = #{qna_Title}
	     , qna_Content = #{qna_Content}
	     , qna_Date = sysdate

	     , qna_Pw = #{qna_Pw}

WHERE qna_Num = #{qna_Num}
