mapper.select:

select res_Num, sp_Name, sp_type, mem_id, cp_name, res_state
from
(select rownum rn, list.* 
from ( 


) list
where list.res_date >= trunc(sysdate) 
and rownum <= #{pageNum} * #{amount}
)

where rn > (#{pageNum} -1) * #{amount}

select rownum rn, list.*
from(

) list

select res_Num, mem_id, res_date, to_char(res_usedate,'yyyy-mm'), res_state, res_amount, sp_Num
	from tb_reserv
where res_num=#{res_Num}

select mem_id, mem_Name, mem_Phone
from tb_mem
where mem_id = #{mem_Id}
