mapper.select:
select s.sp_num, c.cp_name, s.sp_name, s.sp_type, s.sp_stock, s.sp_address, s.sp_exposition, s.sp_service, s.sp_traffic, s.sp_file
from tb_space s, tb_company c
where s.cp_num = c.cp_num and s.sp_num = #{sp_Num}

mapper.update:
update tb_space
 set sp_state = 1
 where sp_num = #{sp_Num}
