mapper.select:
/* Space - spaceList */
select sp_num, sp_file, sp_name, sp_address, sp_price, cp_phone
from
( select list.*, rownum as rnum
from (
select s.sp_num, s.sp_file, s.sp_name, s.sp_address, s.sp_price, c.cp_phone
from tb_space s, tb_company c


	s.cp_num = c.cp_num

		and sp_name like '%' || #{keyword} || '%'



order by sp_num desc
) list
)



			rnum between #{start_row} and #{end_row}


select nvl(count(1), 0) from
( select list.*, rownum as rnum
from(
select * from tb_space


		sp_name like '%' || #{keyword} || '%'


) list
)

/* Board - spaceDetail */
SELECT
	      s.sp_num, s.sp_type, s.sp_stock, s.sp_name, s.sp_price, s.sp_address,
	      s.sp_exposition, s.sp_file, s.sp_service, s.sp_traffic, c.cp_phone
FROM tb_space s, tb_company c
WHERE sp_num = #{sp_Num}
